import React, { Component } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Input,
  Label,
} from "reactstrap";

export default class CustomModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeItem: this.props.activeItem,
    };
  }

  handleChange = (e) => {
    let { name, value } = e.target;

    if (e.target.type === "checkbox") {
      value = e.target.checked;
    }

    const activeItem = { ...this.state.activeItem, [name]: value };

    this.setState({ activeItem });
  };

  render() {
    const { toggle, onSave } = this.props;

    return (
      <Modal isOpen={true} toggle={toggle}>
        <ModalHeader toggle={toggle}>Schedule Info</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup>
              <Label for="todo-search_phrase">Search Pharse</Label>
              <Input
                type="text"
                id="todo-search_phrase"
                name="search_phrase"
                value={this.state.activeItem.search_phrase}
                onChange={this.handleChange}
                placeholder="Enter Search Pharse here"
              />
            </FormGroup>
            <FormGroup>
              <Label for="todo-interval">Search Pharse</Label>
              <Input
                type="text"
                id="todo-interval"
                name="interval"
                value={this.state.activeItem.interval}
                onChange={this.handleChange}
                placeholder="Enter Search Pharse here"
              />
            </FormGroup>
             <FormGroup>
              <Label for="todo-search_phrase">Search Pharse</Label>
              <Input
                type="text"
                id="todo-search_phrase"
                name="search_phrase"
                value={this.state.activeItem.search_phrase}
                onChange={this.handleChange}
                placeholder="Enter Search Pharse here"
              />
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button
            color="success"
            onClick={() => onSave(this.state.activeItem)}
          >
            Save
          </Button>
        </ModalFooter>
      </Modal>
    );
  }
}
